// src/components/AddPlayerModal.js

import React, { useState, useEffect, useRef } from "react";
import axios from "axios";

export default function AddPlayerModal({ open, onClose, onAdded, schools }) {
  const [form, setForm] = useState({
    full_name: "",
    dob: "",
    school_id: "",
    guardian_phone: "",
  });
  const [photoFile, setPhotoFile] = useState(null);
  const [certFile, setCertFile] = useState(null);
  const [loading, setLoading] = useState(false);
  const [err, setErr] = useState("");
  const fileInputPhoto = useRef();
  const fileInputCert = useRef();

  // Reset form on open or when schools change
  useEffect(() => {
    if (open) {
      setForm({
        full_name: "",
        dob: "",
        school_id:
          Array.isArray(schools) && schools.length > 0 && schools[0]?.id
            ? schools[0].id
            : "",
        guardian_phone: "",
      });
      setPhotoFile(null);
      setCertFile(null);
      setErr("");
    }
  }, [open, schools]);

  // Guard against missing schools prop
  if (!open) return null;

  const handleChange = (e) => {
    setForm((f) => ({ ...f, [e.target.name]: e.target.value }));
  };
  const handlePhoto = (e) => setPhotoFile(e.target.files[0]);
  const handleCert = (e) => setCertFile(e.target.files[0]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setErr("");
    try {
      const data = new FormData();
      Object.entries(form).forEach(([k, v]) => data.append(k, v));
      if (photoFile) data.append("photo", photoFile);
      if (certFile) data.append("birth_cert", certFile);

      const token = localStorage.getItem("token");
      const res = await axios.post(
        "http://localhost:5000/api/players/register",
        data,
        { headers: { Authorization: `Bearer ${token}` } }
      );
      onAdded && onAdded(res.data); // Let parent reload
      onClose();
    } catch (error) {
      setErr(error?.response?.data?.message || "Add player failed.");
    }
    setLoading(false);
  };

  // Defensive mapping: only render if schools is a valid array
  const renderSchoolOptions = () => {
    if (!Array.isArray(schools) || schools.length === 0) {
      return <option value="">No schools available</option>;
    }
    return schools.map(
      (s) =>
        s &&
        s.id && (
          <option value={s.id} key={s.id}>
            {s.name}
          </option>
        )
    );
  };

  return (
    <div className="fixed inset-0 flex items-center justify-center z-50 bg-black bg-opacity-30">
      <div className="bg-white p-6 rounded shadow-lg w-full max-w-lg relative">
        <button
          className="absolute top-2 right-3 text-lg"
          onClick={onClose}
          type="button"
        >
          &times;
        </button>
        <h2 className="text-xl font-bold mb-4">Add Player</h2>
        <form onSubmit={handleSubmit} className="space-y-3">
          <div>
            <label className="block text-sm mb-1">Full Name</label>
            <input
              className="w-full border p-2 rounded"
              name="full_name"
              value={form.full_name}
              onChange={handleChange}
              required
            />
          </div>
          <div>
            <label className="block text-sm mb-1">Date of Birth</label>
            <input
              className="w-full border p-2 rounded"
              name="dob"
              type="date"
              value={form.dob}
              onChange={handleChange}
              required
            />
          </div>
          <div>
            <label className="block text-sm mb-1">School</label>
            <select
              className="w-full border p-2 rounded"
              name="school_id"
              value={form.school_id || ""}
              onChange={handleChange}
              required
            >
              <option value="">Select school...</option>
              {renderSchoolOptions()}
            </select>
          </div>
          <div>
            <label className="block text-sm mb-1">Guardian Phone</label>
            <input
              className="w-full border p-2 rounded"
              name="guardian_phone"
              value={form.guardian_phone}
              onChange={handleChange}
              required
            />
          </div>
          <div className="flex gap-4 items-center">
            <div>
              <label className="block text-sm mb-1">Photo</label>
              <input
                type="file"
                ref={fileInputPhoto}
                onChange={handlePhoto}
                accept="image/*"
              />
            </div>
            <div>
              <label className="block text-sm mb-1">Birth Certificate</label>
              <input
                type="file"
                ref={fileInputCert}
                onChange={handleCert}
                accept="image/*,application/pdf"
              />
            </div>
          </div>
          {err && <div className="text-red-500">{err}</div>}
          <button
            type="submit"
            className="w-full bg-blue-600 text-white rounded py-2 mt-2"
            disabled={loading}
          >
            {loading ? "Adding..." : "Add Player"}
          </button>
        </form>
      </div>
    </div>
  );
}
