// src/pages/SchoolDashboard.js

import React, { useEffect, useState } from "react";
import API from "../api/axios";
import { useNavigate } from "react-router-dom";

export default function SchoolDashboard() {
  const [stats, setStats] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const navigate = useNavigate();

  useEffect(() => {
    // Debug token presence
    console.log("Token in localStorage:", localStorage.getItem("token"));

    async function fetchStats() {
      try {
        // Axios interceptor auto-adds token, no need to set headers here
        const res = await API.get("/schools/me");
        setStats(res.data);
      } catch (err) {
        setError("Failed to load dashboard stats.");
      } finally {
        setLoading(false);
      }
    }
    fetchStats();
  }, []);

  if (loading) return <div className="p-6">Loading dashboard...</div>;
  if (error) return <div className="p-6 text-red-600">{error}</div>;

  return (
    <div className="min-h-screen bg-blue-50 p-6">
      <h1 className="text-2xl font-bold mb-4">School Admin Dashboard</h1>

      {/* Overview cards */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-6">
        <div className="bg-white rounded-xl shadow p-4 flex flex-col items-center">
          <span className="text-lg font-semibold">School Name</span>
          <span>{stats.name}</span>
        </div>
        <div className="bg-white rounded-xl shadow p-4 flex flex-col items-center">
          <span className="text-lg font-semibold">Address</span>
          <span>{stats.address}</span>
        </div>
        <div className="bg-white rounded-xl shadow p-4 flex flex-col items-center">
          <span className="text-lg font-semibold">Phone</span>
          <span>{stats.phone}</span>
        </div>
      </div>

      {/* Player List & Actions */}
      <div className="bg-white rounded-xl shadow p-6">
        <div className="flex justify-between items-center mb-4">
          <h2 className="text-xl font-bold">Players</h2>
          <button
            className="bg-blue-600 hover:bg-blue-700 text-white font-semibold px-4 py-2 rounded-lg"
            onClick={() => navigate("/player-list")}
          >
            View All Players
          </button>
        </div>
        <p>
          You can view, add, or bulk upload player profiles from your school here.
        </p>
      </div>
    </div>
  );
}

/**
 * ONBOARDING NOTES:
 * - Axios interceptor automatically adds JWT token for auth.
 * - This page fetches and displays the current school's info.
 * - Add player stats and tournament info as you build more endpoints.
 * - Connect to /player-list page for detailed player management.
 */
