// src/pages/TournamentSetup.jsx
import React, { useState } from "react";
import StepIndicator from "../components/ui/StepIndicator";
// Import step components as we build them (for now placeholders)
import StepSports from "../components/Tournament/StepSports";
import StepSportDetails from "../components/Tournament/StepSportDetails";
import StepFormat from "../components/Tournament/StepFormat";
import StepReview from "../components/Tournament/StepReview";

export default function TournamentSetup() {
  // Wizard state
  const [step, setStep] = useState(0);
  const [wizardData, setWizardData] = useState({
    sports: [],         // [{ sportId, name, icon }]
    details: [],        // For each selected sport: gender/age/weight/teamSize/teams
    format: {},         // For each sport: format type, bracket config
    // ...other fields as needed
  });

  // Navigation helpers
  const next = () => setStep((s) => Math.min(s + 1, 3));
  const prev = () => setStep((s) => Math.max(s - 1, 0));

  // Render step content
  function renderStep() {
    switch (step) {
      case 0:
        return (
          <StepSports
            value={wizardData.sports}
            onChange={sports => setWizardData(d => ({ ...d, sports }))}
            onNext={next}
          />
        );
      case 1:
        return (
          <StepSportDetails
            sports={wizardData.sports}
            value={wizardData.details}
            onChange={details => setWizardData(d => ({ ...d, details }))}
            onNext={next}
            onBack={prev}
          />
        );
      case 2:
        return (
          <StepFormat
            sports={wizardData.sports}
            details={wizardData.details}
            value={wizardData.format}
            onChange={format => setWizardData(d => ({ ...d, format }))}
            onNext={next}
            onBack={prev}
          />
        );
      case 3:
        return (
          <StepReview
            data={wizardData}
            onBack={prev}
            // onSubmit={...}
          />
        );
      default:
        return null;
    }
  }

  return (
    <div className="max-w-3xl mx-auto py-10">
      <StepIndicator current={step} />
      <div className="bg-white rounded-lg shadow-lg p-8">{renderStep()}</div>
    </div>
  );
}
