import React, { useState, useEffect } from "react";
import axios from "axios";

export default function EditPlayerModal({ open, onClose, player, schools, onUpdated }) {
  const [form, setForm] = useState({
    full_name: "",
    dob: "",
    school_id: "",
    guardian_phone: ""
  });
  const [loading, setLoading] = useState(false);
  const [err, setErr] = useState("");

  useEffect(() => {
    if (player) {
      setForm({
        full_name: player.full_name || "",
        dob: player.dob ? player.dob.substring(0, 10) : "",
        school_id: player.school_id || "",
        guardian_phone: player.guardian_phone || ""
      });
      setErr("");
    }
  }, [player]);

  if (!open) return null;

  const handleChange = (e) => {
    setForm((f) => ({ ...f, [e.target.name]: e.target.value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setErr("");
    try {
      const token = localStorage.getItem("token");
      await axios.patch(
        `http://localhost:5000/api/players/${player.id}`,
        form,
        { headers: { Authorization: `Bearer ${token}` } }
      );
      onUpdated && onUpdated();
      onClose();
    } catch (error) {
      setErr(error?.response?.data?.message || "Update failed.");
    }
    setLoading(false);
  };

  return (
    <div className="fixed inset-0 flex items-center justify-center z-50 bg-black bg-opacity-30">
      <div className="bg-white p-6 rounded shadow-lg w-full max-w-md relative">
        <button className="absolute top-2 right-3 text-lg" onClick={onClose} type="button">
          &times;
        </button>
        <h2 className="text-xl font-bold mb-4">Edit Player</h2>
        <form onSubmit={handleSubmit} className="space-y-3">
          <div>
            <label className="block text-sm mb-1">Full Name</label>
            <input
              name="full_name"
              value={form.full_name}
              onChange={handleChange}
              required
              className="w-full border p-2 rounded"
            />
          </div>
          <div>
            <label className="block text-sm mb-1">Date of Birth</label>
            <input
              type="date"
              name="dob"
              value={form.dob}
              onChange={handleChange}
              required
              className="w-full border p-2 rounded"
            />
          </div>
          <div>
            <label className="block text-sm mb-1">School</label>
            <select
              name="school_id"
              value={form.school_id}
              onChange={handleChange}
              required
              className="w-full border p-2 rounded"
            >
              {schools.map((s) => (
                <option key={s.id} value={s.id}>
                  {s.name}
                </option>
              ))}
            </select>
          </div>
          <div>
            <label className="block text-sm mb-1">Guardian Phone</label>
            <input
              name="guardian_phone"
              value={form.guardian_phone}
              onChange={handleChange}
              required
              className="w-full border p-2 rounded"
            />
          </div>
          {err && <div className="text-red-500">{err}</div>}
          <button
            type="submit"
            disabled={loading}
            className="w-full bg-blue-600 text-white rounded py-2 mt-2"
          >
            {loading ? "Updating..." : "Update Player"}
          </button>
        </form>
      </div>
    </div>
  );
}
