// src/components/Tournament/StepMatchFormat.jsx

import React from "react";
import { Input } from "@/components/ui/input";

const FORMAT_TYPES = [
  "knockout",
  "round robin",
  "group + knockout",
  "league",
  "custom"
];

function StepMatchFormat({ categories, matchConfigs, setMatchConfigs, nextStep }) {
  const handleChange = (code, field, value) => {
    setMatchConfigs((prev) => ({
      ...prev,
      [code]: {
        ...prev[code],
        [field]: value,
      },
    }));
  };

  return (
    <div className="p-6 max-w-5xl mx-auto">
      <h2 className="text-2xl font-bold mb-6 text-athletiq-navy">Step 3: Match Format Setup</h2>

      {categories.map((cat) => {
        const config = matchConfigs[cat.categoryCode] || {};
        return (
          <div
            key={cat.categoryCode}
            className="border rounded-xl p-5 mb-6 bg-white shadow-md hover:shadow-lg transition"
          >
            <div className="flex justify-between items-center mb-3">
              <div>
                <h3 className="text-lg font-semibold text-athletiq-blue">
                  {cat.sportName} – {cat.gender} – {cat.ageGroup}
                </h3>
                <p className="text-sm text-gray-500">Category: {cat.categoryCode}</p>
              </div>
            </div>

            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <div>
                <label className="block text-sm font-medium mb-1">Format Type</label>
                <select
                  value={config.formatType || "knockout"}
                  onChange={(e) => handleChange(cat.categoryCode, "formatType", e.target.value)}
                  className="w-full border p-2 rounded"
                >
                  {FORMAT_TYPES.map((type) => (
                    <option key={type} value={type}>
                      {type.charAt(0).toUpperCase() + type.slice(1)}
                    </option>
                  ))}
                </select>
              </div>

              <div>
                <label className="block text-sm font-medium mb-1">Match Duration (mins)</label>
                <Input
                  type="number"
                  value={config.matchDuration || 40}
                  onChange={(e) => handleChange(cat.categoryCode, "matchDuration", e.target.value)}
                  className="w-full"
                />
              </div>

              {(config.formatType === "round robin" || config.formatType === "group + knockout") && (
                <>
                  <div>
                    <label className="block text-sm font-medium mb-1">Points (Win)</label>
                    <Input
                      type="number"
                      value={config.pointsWin || 3}
                      onChange={(e) => handleChange(cat.categoryCode, "pointsWin", e.target.value)}
                    />
                  </div>
                  <div>
                    <label className="block text-sm font-medium mb-1">Points (Draw)</label>
                    <Input
                      type="number"
                      value={config.pointsDraw || 1}
                      onChange={(e) => handleChange(cat.categoryCode, "pointsDraw", e.target.value)}
                    />
                  </div>
                  <div>
                    <label className="block text-sm font-medium mb-1">Points (Loss)</label>
                    <Input
                      type="number"
                      value={config.pointsLoss || 0}
                      onChange={(e) => handleChange(cat.categoryCode, "pointsLoss", e.target.value)}
                    />
                  </div>
                  <div>
                    <label className="block text-sm font-medium mb-1">Number of Groups</label>
                    <Input
                      type="number"
                      value={config.groups || 1}
                      onChange={(e) => handleChange(cat.categoryCode, "groups", e.target.value)}
                    />
                  </div>
                </>
              )}

              {config.formatType === "knockout" && (
                <div>
                  <label className="block text-sm font-medium mb-1">Third Place Match?</label>
                  <select
                    value={config.thirdPlace || "no"}
                    onChange={(e) => handleChange(cat.categoryCode, "thirdPlace", e.target.value)}
                    className="w-full border p-2 rounded"
                  >
                    <option value="yes">Yes</option>
                    <option value="no">No</option>
                  </select>
                </div>
              )}
            </div>
          </div>
        );
      })}

      <div className="flex justify-end mt-6">
        <button
          onClick={nextStep}
          className="bg-athletiq-green hover:bg-green-700 text-white px-6 py-2 rounded shadow"
        >
          Continue to Review & Confirm
        </button>
      </div>
    </div>
  );
}

export default StepMatchFormat;
