// src/pages/SchoolDashboard.js
import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';

export default function SchoolDashboard() {
  const navigate = useNavigate();
  const [schoolName, setSchoolName] = useState('');
  const [schoolId, setSchoolId] = useState('');

  useEffect(() => {
    // Check login and get school info from localStorage
    const token = localStorage.getItem('token');
    const role = localStorage.getItem('role');
    const storedSchoolId = localStorage.getItem('school_id');
    const storedSchoolName = localStorage.getItem('school_name') || '';
    if (!token || role !== 'school_admin') {
      navigate('/login');
      return;
    }
    setSchoolId(storedSchoolId || '');
    setSchoolName(storedSchoolName || '');
  }, [navigate]);

  const handleLogout = () => {
    localStorage.clear();
    navigate('/login');
  };

  return (
    <div className="flex flex-col items-center min-h-screen pt-12">
      <div className="w-full max-w-2xl bg-white p-8 rounded shadow">
        <h1 className="text-2xl font-bold mb-2">üè´ School Dashboard</h1>
        <p className="mb-6">Welcome, School Admin!</p>
        <div className="mb-4">
          <strong>School Name:</strong> {schoolName || "N/A"}<br />
          <strong>School ID:</strong> {schoolId || "N/A"}
        </div>
        <div className="flex gap-4 mb-6">
          <button className="px-4 py-2 bg-green-500 text-white rounded"
            onClick={() => navigate('/register-player')}>
            Register Player
          </button>
          <button className="px-4 py-2 bg-blue-500 text-white rounded"
            onClick={() => navigate('/player-list')}>
            Player List
          </button>
          <button className="px-4 py-2 bg-purple-500 text-white rounded"
            onClick={() => navigate('/tournaments')}>
            Tournaments
          </button>
        </div>
        <button className="mt-4 px-3 py-2 bg-red-400 text-white rounded" onClick={handleLogout}>
          Logout
        </button>
      </div>
    </div>
  );
}
