import React, { useEffect, useState } from "react";
import axios from "axios";
import DashboardLayout from "../components/DashboardLayout";  // Assuming this exists and is working

export default function AdminDashboard() {
  // State variables
  const [schools, setSchools] = useState([]);
  const [players, setPlayers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [err, setErr] = useState("");

  const token = localStorage.getItem("token"); // JWT token
  const user = JSON.parse(localStorage.getItem("user") || "{}"); // User data

  // Fetch schools and players data on mount or token change
  useEffect(() => {
    const fetchData = async () => {
      setLoading(true);
      setErr("");
      try {
        // Fetch schools and players concurrently
        const [schoolsRes, playersRes] = await Promise.all([
          axios.get("http://localhost:5000/api/schools", {
            headers: { Authorization: `Bearer ${token}` },
          }),
          axios.get(
            `http://localhost:5000/api/players${
              user.role === "super_admin" && user.school_id
                ? `?school_id=${user.school_id}`
                : ""
            }`,
            { headers: { Authorization: `Bearer ${token}` } }
          ),
        ]);

        console.log("Schools Response:", schoolsRes.data);  // Log the schools data
        console.log("Players Response:", playersRes.data);  // Log the players data

        setSchools(schoolsRes.data.schools || []);
        setPlayers(playersRes.data.players || []);
      } catch (error) {
        console.error("Error fetching data:", error);  // Log error
        setErr(error?.response?.data?.message || "Error loading data.");
      }
      setLoading(false);
    };

    fetchData();
  }, [token, user]);

  return (
    <DashboardLayout user={user} onLogout={() => {
      localStorage.removeItem("token");
      localStorage.removeItem("user");
      window.location.href = "/login";
    }}>
      <div className="flex flex-col gap-6 w-full">
        <h1 className="text-3xl font-extrabold text-athletiq-navy mb-2">Admin Dashboard</h1>

        {err && <div className="text-red-500 mb-4">{err}</div>}
        
        {loading ? (
          <div>Loading...</div>
        ) : (
          <>
            {/* -------- Schools View -------- */}
            <div>
              <h2 className="text-xl font-bold text-athletiq-navy mb-4">All Schools</h2>
              <div className="overflow-x-auto bg-white rounded-lg shadow-md">
                <table className="min-w-full text-sm">
                  <thead>
                    <tr className="bg-athletiq-blue text-white">
                      <th className="p-2 text-left">Name</th>
                      <th className="p-2 text-left">Address</th>
                      <th className="p-2 text-left">Email</th>
                      <th className="p-2 text-left">Active</th>
                      <th className="p-2 text-left">Actions</th>
                    </tr>
                  </thead>
                  <tbody>
                    {schools.map((school) => (
                      <tr key={school.id} className="border-b last:border-none">
                        <td className="p-2">{school.name}</td>
                        <td className="p-2">{school.address}</td>
                        <td className="p-2">{school.email}</td>
                        <td className="p-2">{school.is_active ? "Yes" : "No"}</td>
                        <td className="p-2">
                          <button className="bg-athletiq-blue text-white px-3 py-1 rounded hover:bg-athletiq-navy">
                            Edit
                          </button>
                          <button className="bg-red-500 text-white px-3 py-1 rounded hover:bg-red-700">
                            Delete
                          </button>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            </div>

            {/* -------- Players View -------- */}
            <div>
              <h2 className="text-xl font-bold text-athletiq-navy mb-4">All Players</h2>
              <div className="overflow-x-auto bg-white rounded-lg shadow-md">
                <table className="min-w-full text-sm">
                  <thead>
                    <tr className="bg-athletiq-blue text-white">
                      <th className="p-2 text-left">Name</th>
                      <th className="p-2 text-left">School</th>
                      <th className="p-2 text-left">DOB</th>
                      <th className="p-2 text-left">Actions</th>
                    </tr>
                  </thead>
                  <tbody>
                    {players.map((player) => (
                      <tr key={player.id} className="border-b last:border-none">
                        <td className="p-2">{player.full_name}</td>
                        <td className="p-2">{player.school_name || player.school_id}</td>
                        <td className="p-2">{player.dob}</td>
                        <td className="p-2">
                          <button className="bg-athletiq-blue text-white px-3 py-1 rounded hover:bg-athletiq-navy">
                            View
                          </button>
                          <button className="bg-athletiq-green text-white px-3 py-1 rounded hover:bg-green-700">
                            Edit
                          </button>
                          <button className="bg-red-500 text-white px-3 py-1 rounded hover:bg-red-700">
                            Delete
                          </button>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            </div>
          </>
        )}
      </div>
    </DashboardLayout>
  );
}
