// src/components/BulkPlayerUploadModal.js
import React, { useRef, useState } from "react";
import axios from "axios";

export default function BulkPlayerUploadModal({ open, onClose, onUploaded }) {
  const fileRef = useRef();
  const [uploading, setUploading] = useState(false);
  const [result, setResult] = useState(null);
  const [error, setError] = useState("");
  const token = localStorage.getItem("token");

  if (!open) return null;

  const handleUpload = async (e) => {
    e.preventDefault();
    setError("");
    setResult(null);
    setUploading(true);

    const formData = new FormData();
    formData.append("csv", fileRef.current.files[0]);
    try {
      const res = await axios.post(
        "http://localhost:5000/api/players/bulk-upload",
        formData,
        { headers: { Authorization: `Bearer ${token}`, "Content-Type": "multipart/form-data" } }
      );
      setResult(res.data);
      if (onUploaded) onUploaded();
    } catch (err) {
      setError(err?.response?.data?.message || "Upload failed.");
    }
    setUploading(false);
  };

  // CSV template as download
  const downloadTemplate = () => {
    const csv =
      `full_name,dob,school_id\n` +
      `Ramesh Thapa,2007-06-12,1\nSunita Lama,2008-08-25,2\n`;
    const blob = new Blob([csv], { type: "text/csv" });
    const url = window.URL.createObjectURL(blob);
    const a = document.createElement("a");
    a.href = url;
    a.download = "players_template.csv";
    a.click();
    window.URL.revokeObjectURL(url);
  };

  return (
    <div className="fixed inset-0 bg-black bg-opacity-30 flex items-center justify-center z-50">
      <div className="bg-white rounded-lg p-8 shadow max-w-lg w-full">
        <h2 className="text-xl font-bold mb-3">Bulk Upload Players (CSV)</h2>
        <p className="mb-2 text-sm text-athletiq-navy">
          Upload a CSV file with player data.{" "}
          <button
            className="text-athletiq-green underline"
            onClick={downloadTemplate}
          >
            Download Template
          </button>
        </p>
        <form onSubmit={handleUpload}>
          <input type="file" accept=".csv" ref={fileRef} className="mb-3" required />
          <div className="flex gap-3 mt-4">
            <button
              type="submit"
              className="bg-athletiq-green text-white px-4 py-2 rounded"
              disabled={uploading}
            >
              {uploading ? "Uploading..." : "Upload"}
            </button>
            <button
              type="button"
              className="bg-gray-200 px-4 py-2 rounded"
              onClick={onClose}
              disabled={uploading}
            >
              Cancel
            </button>
          </div>
        </form>
        {error && <div className="text-red-500 mt-3">{error}</div>}
        {result && (
          <div className="mt-3 text-sm">
            <div>✅ Success: {result.successCount}</div>
            <div>❌ Failed: {result.failCount}</div>
            {result.errors && result.errors.length > 0 && (
              <ul className="mt-2 text-red-500">
                {result.errors.map((e, i) => (
                  <li key={i}>{e}</li>
                ))}
              </ul>
            )}
          </div>
        )}
      </div>
    </div>
  );
}
