import React from "react";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import Home from "./pages/Home";
import Login from "./pages/Login";
import Register from "./pages/Register";
import SchoolRegistrationWizard from './pages/SchoolRegistrationWizard';
import SchoolDashboard from './pages/SchoolDashboard';
import PlayerProfile from "./pages/PlayerProfile";
import PlayerRegister from "./pages/PlayerRegister";
import AdminDashboard from "./pages/AdminDashboard";
import PlayerList from "./pages/PlayerList";
import Unauthorized from "./pages/Unauthorized";
import ProtectedRoute from "./components/ProtectedRoute";

import "./i18n";

export default function App() {
  return (
    <Router>
      <div className="min-h-screen bg-blue-50">
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/login" element={<Login />} />
          <Route path="/register" element={<Register />} />
          <Route path="/register-player" element={<PlayerRegister />} />

          {/* School Onboarding Wizard (school_admin only) */}
          <Route path="/school-onboarding" element={
            <ProtectedRoute role="school_admin">
              <SchoolRegistrationWizard />
            </ProtectedRoute>
          } />

          {/* School Dashboard (school_admin only) */}
          <Route path="/school-dashboard" element={
            <ProtectedRoute role="school_admin">
              <SchoolDashboard />
            </ProtectedRoute>
          } />

          {/* Admin Dashboard (super_admin only) */}
          <Route path="/admin" element={
            <ProtectedRoute role="super_admin">
              <AdminDashboard />
            </ProtectedRoute>
          } />

          {/* Player Profile (any logged-in user) */}
          <Route path="/player/:id" element={
            <ProtectedRoute>
              <PlayerProfile />
            </ProtectedRoute>
          } />

          {/* Player List (any logged-in user) */}
          <Route path="/player-list" element={
            <ProtectedRoute>
              <PlayerList />
            </ProtectedRoute>
          } />

          {/* Unauthorized page */}
          <Route path="/unauthorized" element={<Unauthorized />} />

          {/* 404 fallback */}
          <Route path="*" element={<div className="p-8 text-center">404 Not Found</div>} />
        </Routes>
      </div>
    </Router>
  );
}
