// src/components/Sidebar.js

import React from "react";
import {
  Home,
  Users,
  UserPlus,
  Upload,
  School,
  Trophy,
  Settings,
} from "lucide-react"; // Lucide icons

const navItems = [
  { label: "Dashboard", icon: <Home size={20} />, key: "dashboard", path: "/school-dashboard" },
  { label: "Player Roster", icon: <Users size={20} />, key: "roster", path: "/school-dashboard/roster" },
  { label: "Add Player", icon: <UserPlus size={20} />, key: "add", path: "/school-dashboard/add" },
  { label: "Bulk Upload", icon: <Upload size={20} />, key: "bulk", path: "/school-dashboard/bulk" },
  { label: "School Profile", icon: <School size={20} />, key: "profile", path: "/school-dashboard/profile" },
  { label: "Tournament Stats", icon: <Trophy size={20} />, key: "tournaments", path: "/school-dashboard/tournaments" },
  { label: "Settings", icon: <Settings size={20} />, key: "settings", path: "/school-dashboard/settings" },
];

export default function Sidebar({ active }) {
  return (
    <aside className="bg-[#092642] text-white min-h-screen w-64 flex flex-col shadow-xl">
      <div className="flex items-center gap-2 px-6 h-20 border-b border-[#174167]">
        <img src="/logo192.png" alt="ATHLETIQ Logo" className="w-10 h-10" />
        <span className="text-2xl font-extrabold tracking-wide">ATHLETIQ</span>
      </div>
      <nav className="flex-1 py-4">
        {navItems.map((item) => (
          <a
            key={item.key}
            href={item.path}
            className={`flex items-center px-6 py-3 gap-3 text-base font-medium transition 
              ${
                active === item.key
                  ? "bg-[#174167] text-[#63D471]" // highlight
                  : "hover:bg-[#174167] hover:text-[#63D471] text-white"
              }`}
          >
            {item.icon}
            {item.label}
          </a>
        ))}
      </nav>
    </aside>
  );
}
