// src/pages/AdminDashboard.js

import React, { useEffect, useState } from "react";
import axios from "axios";
import DashboardLayout from "../components/DashboardLayout";
import AddSchoolModal from "../components/AddSchoolModal";
import EditSchoolModal from "../components/EditSchoolModal";
import BulkSchoolUploadModal from "../components/BulkSchoolUploadModal"; // NEW
import AddPlayerModal from "../components/AddPlayerModal";
import EditPlayerModal from "../components/EditPlayerModal";
import ViewPlayerModal from "../components/ViewPlayerModal";
import BulkPlayerUploadModal from "../components/BulkPlayerUploadModal";
import ChangeAdminPasswordModal from "../components/ChangeAdminPasswordModal";

export default function AdminDashboard() {
  // State variables
  const [schools, setSchools] = useState([]);
  const [players, setPlayers] = useState([]);
  const [view, setView] = useState("schools");
  const [loading, setLoading] = useState(true);
  const [stats, setStats] = useState({ schools: 0, players: 0 });
  const [err, setErr] = useState("");
  const [changePwdSchool, setChangePwdSchool] = useState(null);

  // Modals/UI state
  const [addSchoolOpen, setAddSchoolOpen] = useState(false);
  const [bulkSchoolOpen, setBulkSchoolOpen] = useState(false); // NEW
  const [editSchool, setEditSchool] = useState(null);

  const [addPlayerOpen, setAddPlayerOpen] = useState(false);
  const [editPlayer, setEditPlayer] = useState(null);
  const [viewPlayer, setViewPlayer] = useState(null);

  const [bulkPlayerOpen, setBulkPlayerOpen] = useState(false);

  const token = localStorage.getItem("token");
  const user = JSON.parse(localStorage.getItem("user") || "{}");

  // Fetch schools and players on mount or token change
  useEffect(() => {
    async function fetchData() {
      setLoading(true);
      setErr("");
      try {
        const [schoolsRes, playersRes] = await Promise.all([
          axios.get("http://localhost:5000/api/schools", {
            headers: { Authorization: `Bearer ${token}` },
          }),
          axios.get("http://localhost:5000/api/players", {
            headers: { Authorization: `Bearer ${token}` },
          }),
        ]);
        setSchools(schoolsRes.data.schools || []);
        setPlayers(playersRes.data.players || []);
        setStats({
          schools: schoolsRes.data.schools?.length || 0,
          players: playersRes.data.players?.length || 0,
        });
      } catch (error) {
        setErr(error?.response?.data?.message || "Error loading data.");
      }
      setLoading(false);
    }
    fetchData();
  }, [token]);

  // Reload helpers
  const reloadPlayers = () =>
    axios
      .get("http://localhost:5000/api/players", {
        headers: { Authorization: `Bearer ${token}` },
      })
      .then((res) => setPlayers(res.data.players || []))
      .catch(() => {});

  const reloadSchools = () =>
    axios
      .get("http://localhost:5000/api/schools", {
        headers: { Authorization: `Bearer ${token}` },
      })
      .then((res) => setSchools(res.data.schools || []))
      .catch(() => {});

  // Delete handlers
  const handleDeleteSchool = async (id) => {
    if (!window.confirm("Are you sure you want to delete this school?")) return;
    try {
      await axios.delete(`http://localhost:5000/api/schools/${id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      reloadSchools();
    } catch (error) {
      alert(error?.response?.data?.message || "Delete failed.");
    }
  };

  const handleDeletePlayer = async (id) => {
    if (!window.confirm("Are you sure you want to delete this player?")) return;
    try {
      await axios.delete(`http://localhost:5000/api/players/${id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      reloadPlayers();
    } catch (error) {
      alert(error?.response?.data?.message || "Delete failed.");
    }
  };

  // Logout
  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("user");
    window.location.href = "/login";
  };

  // Sidebar links for navigation
  const sidebarLinks = [
    { label: "Schools", icon: "ðŸ«", onClick: () => setView("schools"), active: view === "schools" },
    { label: "Players", icon: "ðŸ§‘â€ðŸŽ“", onClick: () => setView("players"), active: view === "players" },
    { label: "Platform Stats", icon: "ðŸ“Š", onClick: () => setView("stats"), active: view === "stats" },
  ];

  // Handle change admin password
  const handleOpenPwdChange = (school) => setChangePwdSchool(school);

  return (
    <DashboardLayout user={user} onLogout={handleLogout} sidebarLinks={sidebarLinks}>
      <div className="flex flex-col gap-6 w-full">
        <h1 className="text-3xl font-extrabold text-athletiq-navy mb-2">Super Admin Dashboard</h1>
        {err && <div className="text-red-500 mb-4">{err}</div>}
        {loading ? (
          <div>Loading...</div>
        ) : (
          <>
            {/* -------- Schools View -------- */}
            {view === "schools" && (
              <div>
                <div className="flex items-center justify-between mb-4">
                  <h2 className="text-xl font-bold text-athletiq-navy">All Schools</h2>
                  <div className="flex gap-2">
                    <button
                      className="px-4 py-2 bg-athletiq-green text-white rounded hover:bg-green-700 font-semibold"
                      onClick={() => setAddSchoolOpen(true)}
                    >
                      + Add School
                    </button>
                    <button
                      className="px-4 py-2 bg-athletiq-blue text-white rounded hover:bg-athletiq-navy font-semibold"
                      onClick={() => setBulkSchoolOpen(true)}
                    >
                      Bulk Upload Schools
                    </button>
                  </div>
                </div>
                <div className="overflow-x-auto bg-white rounded-lg shadow-md">
                  <table className="min-w-full text-sm">
                    <thead>
                      <tr className="bg-athletiq-blue text-white">
                        <th className="p-2 text-left">Name</th>
                        <th className="p-2 text-left">Address</th>
                        <th className="p-2 text-left">Email</th>
                        <th className="p-2 text-left">Active</th>
                        <th className="p-2 text-left">Logo</th>
                        <th className="p-2 text-left">Actions</th>
                      </tr>
                    </thead>
                    <tbody>
                      {schools.map((s) => (
                        <tr key={s.id} className="border-b last:border-none">
                          <td className="p-2">{s.name}</td>
                          <td className="p-2">{s.address}</td>
                          <td className="p-2">{s.email}</td>
                          <td className="p-2">{s.is_active ? "Yes" : "No"}</td>
                          <td className="p-2">
                            {s.logo_url ? (
                              <img
                                src={`http://localhost:5000/uploads/${s.logo_url}`}
                                alt="logo"
                                className="h-8 w-8 object-cover rounded"
                              />
                            ) : (
                              "-"
                            )}
                          </td>
                          <td className="p-2 flex flex-wrap gap-2">
                            <button
                              className="bg-athletiq-blue text-white px-3 py-1 rounded hover:bg-athletiq-navy"
                              onClick={() => setEditSchool(s)}
                            >
                              Edit
                            </button>
                            <button
                              className="bg-red-500 text-white px-3 py-1 rounded hover:bg-red-700"
                              onClick={() => handleDeleteSchool(s.id)}
                            >
                              Delete
                            </button>
                            <button
                              className="bg-athletiq-green text-white px-3 py-1 rounded hover:bg-green-700"
                              onClick={() => handleOpenPwdChange(s)}
                              title="Change Admin Password"
                            >
                              Change Admin Password
                            </button>
                          </td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                </div>
                <AddSchoolModal
                  open={addSchoolOpen}
                  onClose={() => setAddSchoolOpen(false)}
                  onAdded={reloadSchools}
                />
                <EditSchoolModal
                  school={editSchool}
                  open={!!editSchool}
                  onClose={() => setEditSchool(null)}
                  onUpdated={reloadSchools}
                />
                {/* Bulk School Upload Modal */}
                <BulkSchoolUploadModal
                  open={bulkSchoolOpen}
                  onClose={() => setBulkSchoolOpen(false)}
                  onUploaded={reloadSchools}
                />
                {/* Change Admin Password Modal */}
                <ChangeAdminPasswordModal
                  open={!!changePwdSchool}
                  school={changePwdSchool}
                  onClose={() => setChangePwdSchool(null)}
                  onChanged={reloadSchools}
                />
              </div>
            )}

            {/* -------- Players View -------- */}
            {view === "players" && (
              <div>
                <div className="flex items-center justify-between mb-4">
                  <h2 className="text-xl font-bold text-athletiq-navy">All Players</h2>
                  <div className="flex gap-3">
                    <button
                      className="px-4 py-2 bg-athletiq-green text-white rounded hover:bg-green-700 font-semibold"
                      onClick={() => setAddPlayerOpen(true)}
                    >
                      + Add Player
                    </button>
                    <button
                      className="px-4 py-2 bg-athletiq-blue text-white rounded hover:bg-athletiq-navy font-semibold"
                      onClick={() => setBulkPlayerOpen(true)}
                    >
                      Bulk Upload Players
                    </button>
                  </div>
                </div>
                <div className="overflow-x-auto bg-white rounded-lg shadow-md">
                  <table className="min-w-full text-sm">
                    <thead>
                      <tr className="bg-athletiq-blue text-white">
                        <th className="p-2 text-left">Name</th>
                        <th className="p-2 text-left">School</th>
                        <th className="p-2 text-left">DOB</th>
                        <th className="p-2 text-left">Phone</th>
                        <th className="p-2 text-left">Actions</th>
                      </tr>
                    </thead>
                    <tbody>
                      {players.map((p) => (
                        <tr key={p.id} className="border-b last:border-none">
                          <td className="p-2">{p.full_name}</td>
                          <td className="p-2">{p.school_name || p.school_id}</td>
                          <td className="p-2">{p.dob}</td>
                          <td className="p-2">{p.guardian_phone}</td>
                          <td className="p-2 flex gap-2">
                            <button
                              className="bg-athletiq-blue text-white px-3 py-1 rounded hover:bg-athletiq-navy"
                              onClick={() => setViewPlayer(p)}
                            >
                              View
                            </button>
                            <button
                              className="bg-athletiq-green text-white px-3 py-1 rounded hover:bg-green-700"
                              onClick={() => setEditPlayer(p)}
                            >
                              Edit
                            </button>
                            <button
                              className="bg-red-500 text-white px-3 py-1 rounded hover:bg-red-700"
                              onClick={() => handleDeletePlayer(p.id)}
                            >
                              Delete
                            </button>
                          </td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                </div>
                {/* Player Modals */}
                <AddPlayerModal
                  open={addPlayerOpen}
                  onClose={() => setAddPlayerOpen(false)}
                  onAdded={reloadPlayers}
                  schools={schools}
                />
                <EditPlayerModal
                  open={!!editPlayer}
                  onClose={() => setEditPlayer(null)}
                  player={editPlayer}
                  schools={schools}
                  onUpdated={reloadPlayers}
                />
                <ViewPlayerModal
                  open={!!viewPlayer}
                  onClose={() => setViewPlayer(null)}
                  player={viewPlayer}
                />
                <BulkPlayerUploadModal
                  open={bulkPlayerOpen}
                  onClose={() => setBulkPlayerOpen(false)}
                  onUploaded={reloadPlayers}
                />
              </div>
            )}

            {/* -------- Stats View -------- */}
            {view === "stats" && (
              <div>
                <h2 className="text-xl font-bold text-athletiq-navy mb-4">Platform Stats</h2>
                <div className="flex gap-8">
                  <div className="bg-athletiq-blue px-6 py-4 rounded text-center shadow text-white">
                    <div className="text-3xl font-bold">{stats.schools}</div>
                    <div>Schools</div>
                  </div>
                  <div className="bg-athletiq-green px-6 py-4 rounded text-center shadow text-white">
                    <div className="text-3xl font-bold">{stats.players}</div>
                    <div>Players</div>
                  </div>
                </div>
              </div>
            )}
          </>
        )}
      </div>
    </DashboardLayout>
  );
}

/*
ONBOARDING NOTES:
- Clean, modular super admin dashboard.
- Bulk upload for both schools and players (with modals).
- Add/Edit/Delete/Change Password for schools.
- Stats summary for platform growth.
- All actions reload data in real time.
- Fully ready for future expansion (tournaments, analytics, etc.).
*/
