import React, { useEffect, useState } from "react";
import axios from "axios";
import DashboardLayout from "../components/DashboardLayout";
import AddSchoolModal from "../components/AddSchoolModal";
import EditSchoolModal from "../components/EditSchoolModal";
import AddPlayerModal from "../components/AddPlayerModal";
import EditPlayerModal from "../components/EditPlayerModal";
import ViewPlayerModal from "../components/ViewPlayerModal";

export default function AdminDashboard() {
  const [schools, setSchools] = useState([]);
  const [players, setPlayers] = useState([]);
  const [view, setView] = useState("dashboard");
  const [loading, setLoading] = useState(true);
  const [stats, setStats] = useState({ schools: 0, players: 0 });
  const [err, setErr] = useState("");

  const [addSchoolOpen, setAddSchoolOpen] = useState(false);
  const [editSchool, setEditSchool] = useState(null);

  const [addPlayerOpen, setAddPlayerOpen] = useState(false);
  const [editPlayer, setEditPlayer] = useState(null);
  const [viewPlayer, setViewPlayer] = useState(null);

  const token = localStorage.getItem("token");
  const user = JSON.parse(localStorage.getItem("user") || "{}");

  // Fetch schools and players on mount
  useEffect(() => {
    async function fetchData() {
      setLoading(true);
      setErr("");
      try {
        const [schoolsRes, playersRes] = await Promise.all([
          axios.get("http://localhost:5000/api/schools", {
            headers: { Authorization: `Bearer ${token}` }
          }),
          axios.get("http://localhost:5000/api/players", {
            headers: { Authorization: `Bearer ${token}` }
          })
        ]);
        setSchools(schoolsRes.data.schools || []);
        setPlayers(playersRes.data.players || []);
        setStats({
          schools: schoolsRes.data.schools?.length || 0,
          players: playersRes.data.players?.length || 0
        });
      } catch (error) {
        setErr(error?.response?.data?.message || "Error loading data.");
      }
      setLoading(false);
    }
    fetchData();
  }, [token]);

  // Refreshers
  const reloadPlayers = () => {
    axios.get("http://localhost:5000/api/players", {
      headers: { Authorization: `Bearer ${token}` }
    })
      .then(res => setPlayers(res.data.players || []))
      .catch(() => {});
  };
  const reloadSchools = () => {
    axios.get("http://localhost:5000/api/schools", {
      headers: { Authorization: `Bearer ${token}` }
    })
      .then(res => setSchools(res.data.schools || []))
      .catch(() => {});
  };

  // Handlers
  const handleDeleteSchool = async (id) => {
    if (!window.confirm("Are you sure you want to delete this school?")) return;
    try {
      await axios.delete(`http://localhost:5000/api/schools/${id}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      reloadSchools();
    } catch (error) {
      alert(error?.response?.data?.message || "Delete failed.");
    }
  };
  const handleDeletePlayer = async (id) => {
    if (!window.confirm("Are you sure you want to delete this player?")) return;
    try {
      await axios.delete(`http://localhost:5000/api/players/${id}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      reloadPlayers();
    } catch (error) {
      alert(error?.response?.data?.message || "Delete failed.");
    }
  };
  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("user");
    window.location.href = "/login";
  };

  // --- SIDEBAR MENU DEFINITION (customize as you wish) ---
  const sidebarItems = [
    { key: "dashboard", label: "Dashboard", icon: "Home" },
    { key: "schools", label: "Schools", icon: "Building" },
    { key: "players", label: "Players", icon: "Users" },
    { key: "tournaments", label: "Tournaments", icon: "Trophy" },
    { key: "stats", label: "Platform Stats", icon: "BarChart" },
    { key: "settings", label: "Settings", icon: "Settings" }
  ];

  // --- MAIN UI ---
  return (
    <DashboardLayout
      user={user}
      school={null}
      onLogout={handleLogout}
      sidebarItems={sidebarItems}
      current={view}
      onNavigate={setView}
      title="Super Admin Dashboard"
    >
      <div className="flex flex-col gap-3 w-full">
        {/* --- DASHBOARD VIEW: Main Cards --- */}
        {view === "dashboard" && (
          <>
            <div className="mb-4">
              <h1 className="text-3xl font-extrabold text-athletiq-navy mb-2">Welcome, Super Admin</h1>
              <div className="text-athletiq-gray text-lg">Platform Overview & Quick Actions</div>
            </div>
            <div className="grid grid-cols-1 md:grid-cols-4 gap-4 mb-6">
              <div className="bg-athletiq-blue text-white rounded-lg p-5 shadow flex flex-col items-start">
                <div className="font-bold text-lg mb-1">Total Schools</div>
                <div className="text-3xl font-extrabold">{stats.schools}</div>
              </div>
              <div className="bg-athletiq-blue text-white rounded-lg p-5 shadow flex flex-col items-start">
                <div className="font-bold text-lg mb-1">Total Players</div>
                <div className="text-3xl font-extrabold">{stats.players}</div>
              </div>
              <div className="bg-athletiq-blue text-white rounded-lg p-5 shadow flex flex-col items-start">
                <div className="font-bold text-lg mb-1">Pending Verifications</div>
                <div className="text-3xl font-extrabold">5</div>
              </div>
              <div className="bg-athletiq-blue text-white rounded-lg p-5 shadow flex flex-col items-start">
                <div className="font-bold text-lg mb-1">Active Tournaments</div>
                <div className="text-3xl font-extrabold">2</div>
              </div>
            </div>
          </>
        )}

        {/* --- SCHOOLS TABLE --- */}
        {view === "schools" && (
          <div>
            <div className="flex items-center justify-between mb-4">
              <h2 className="text-xl font-semibold text-athletiq-navy">All Schools</h2>
              <button
                className="px-5 py-2 bg-athletiq-green text-white rounded-lg font-semibold hover:bg-green-700 transition"
                onClick={() => setAddSchoolOpen(true)}
              >
                Add School
              </button>
            </div>
            <div className="bg-white rounded-lg shadow-md overflow-x-auto">
              <table className="min-w-full text-sm">
                <thead>
                  <tr className="bg-athletiq-blue text-white">
                    <th className="p-2 text-left">Name</th>
                    <th className="p-2 text-left">Address</th>
                    <th className="p-2 text-left">Email</th>
                    <th className="p-2 text-left">Active</th>
                    <th className="p-2 text-left">Logo</th>
                    <th className="p-2 text-left">Actions</th>
                  </tr>
                </thead>
                <tbody>
                  {schools.map((s) => (
                    <tr key={s.id} className="border-b last:border-none">
                      <td className="p-2">{s.name}</td>
                      <td className="p-2">{s.address}</td>
                      <td className="p-2">{s.email}</td>
                      <td className="p-2">{s.is_active ? "Yes" : "No"}</td>
                      <td className="p-2">
                        {s.logo_url ? (
                          <img
                            src={`http://localhost:5000/uploads/${s.logo_url}`}
                            alt="logo"
                            className="h-8 w-8 object-cover rounded"
                          />
                        ) : (
                          "-"
                        )}
                      </td>
                      <td className="p-2 flex gap-2">
                        <button
                          className="bg-athletiq-blue text-white px-3 py-1 rounded hover:bg-athletiq-navy"
                          onClick={() => setEditSchool(s)}
                        >
                          Edit
                        </button>
                        <button
                          className="bg-red-500 text-white px-3 py-1 rounded hover:bg-red-700"
                          onClick={() => handleDeleteSchool(s.id)}
                        >
                          Delete
                        </button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>
        )}

        {/* --- PLAYERS TABLE --- */}
        {view === "players" && (
          <div>
            <div className="flex items-center justify-between mb-4">
              <h2 className="text-xl font-semibold text-athletiq-navy">All Players</h2>
              <button
                className="px-5 py-2 bg-athletiq-green text-white rounded-lg font-semibold hover:bg-green-700 transition"
                onClick={() => setAddPlayerOpen(true)}
              >
                Add Player
              </button>
            </div>
            <div className="bg-white rounded-lg shadow-md overflow-x-auto">
              <table className="min-w-full text-sm">
                <thead>
                  <tr className="bg-athletiq-blue text-white">
                    <th className="p-2 text-left">Name</th>
                    <th className="p-2 text-left">School</th>
                    <th className="p-2 text-left">DOB</th>
                    <th className="p-2 text-left">Guardian Phone</th>
                    <th className="p-2 text-left">Actions</th>
                  </tr>
                </thead>
                <tbody>
                  {players.map((p) => (
                    <tr key={p.id} className="border-b last:border-none">
                      <td className="p-2">{p.full_name}</td>
                      <td className="p-2">{p.school_name || p.school_id}</td>
                      <td className="p-2">{p.dob}</td>
                      <td className="p-2">{p.guardian_phone}</td>
                      <td className="p-2 flex gap-2">
                        <button
                          className="bg-athletiq-blue text-white px-2 py-1 rounded hover:bg-athletiq-navy"
                          onClick={() => setEditPlayer(p)}
                        >
                          Edit
                        </button>
                        <button
                          className="bg-red-500 text-white px-2 py-1 rounded hover:bg-red-700"
                          onClick={() => handleDeletePlayer(p.id)}
                        >
                          Delete
                        </button>
                        <button
                          className="bg-gray-200 text-athletiq-navy px-2 py-1 rounded hover:bg-gray-300"
                          onClick={() => setViewPlayer(p)}
                        >
                          View
                        </button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
            {/* Modals */}
            <AddPlayerModal
              open={addPlayerOpen}
              onClose={() => setAddPlayerOpen(false)}
              onAdded={reloadPlayers}
              schools={schools}
            />
            <EditPlayerModal
              open={!!editPlayer}
              onClose={() => setEditPlayer(null)}
              player={editPlayer}
              schools={schools}
              onUpdated={reloadPlayers}
            />
            <ViewPlayerModal
              open={!!viewPlayer}
              onClose={() => setViewPlayer(null)}
              player={viewPlayer}
            />
          </div>
        )}

        {/* --- PLATFORM STATS --- */}
        {view === "stats" && (
          <div>
            <h2 className="text-xl font-semibold mb-4 text-athletiq-navy">Platform Stats</h2>
            <div className="flex gap-8">
              <div className="bg-athletiq-blue text-white px-6 py-4 rounded-lg text-center shadow">
                <div className="text-3xl font-bold">{stats.schools}</div>
                <div>Schools</div>
              </div>
              <div className="bg-athletiq-green text-white px-6 py-4 rounded-lg text-center shadow">
                <div className="text-3xl font-bold">{stats.players}</div>
                <div>Players</div>
              </div>
            </div>
          </div>
        )}

        {/* --- SETTINGS or TOURNAMENTS: Just placeholder for now --- */}
        {view === "tournaments" && (
          <div>
            <h2 className="text-xl font-semibold mb-4 text-athletiq-navy">Tournaments (Coming Soon)</h2>
          </div>
        )}
        {view === "settings" && (
          <div>
            <h2 className="text-xl font-semibold mb-4 text-athletiq-navy">Settings (Coming Soon)</h2>
          </div>
        )}
      </div>

      {/* --- Add/Edit School Modals --- */}
      <AddSchoolModal
        open={addSchoolOpen}
        onClose={() => setAddSchoolOpen(false)}
        onAdded={reloadSchools}
      />
      <EditSchoolModal
        school={editSchool}
        open={!!editSchool}
        onClose={() => setEditSchool(null)}
        onUpdated={reloadSchools}
      />
    </DashboardLayout>
  );
}
