// src/pages/AdminDashboard.js
import React, { useEffect, useState } from "react";
import axios from "axios";
import DashboardLayout from "../components/DashboardLayout";
import Tabs from "../components/Tabs";
import SchoolsTab from "../components/AdminDashboard/SchoolsTab";
import PlayersTab from "../components/AdminDashboard/PlayersTab";
import TournamentsTab from "../components/AdminDashboard/TournamentsTab";
import StatsTab from "../components/AdminDashboard/StatsTab";

// ...all your state and fetch logic as before...

export default function AdminDashboard() {
  // ...state definitions as you had...
  // fetch logic, reload helpers, handlers, etc...

  // Tabs array
  const tabList = ["Schools", "Players", "Tournaments", "Stats"];

  // (Everything else is nearly the same, except this render section!)
  return (
    <DashboardLayout /* ...props... */>
      <div className="flex flex-col gap-6 w-full">
        <h1 className="text-3xl font-extrabold text-athletiq-navy mb-2">Super Admin Dashboard</h1>
        <Tabs tabs={tabList} active={view} onChange={setView} />
        {err && <div className="text-red-500 mb-4">{err}</div>}
        {loading ? (
          <div>Loading...</div>
        ) : (
          <>
            {view === "Schools" && (
              <SchoolsTab
                {...{ schools, addSchoolOpen, setAddSchoolOpen, bulkSchoolOpen, setBulkSchoolOpen, editSchool, setEditSchool, changePwdSchool, setChangePwdSchool, reloadSchools, handleDeleteSchool, loading, err }}
              />
            )}
            {view === "Players" && (
              <PlayersTab
                {...{ players, schools, user, selectedSchoolId, setSelectedSchoolId, addPlayerOpen, setAddPlayerOpen, bulkPlayerOpen, setBulkPlayerOpen, editPlayer, setEditPlayer, viewPlayer, setViewPlayer, selectedPlayerIds, setSelectedPlayerIds, handleDeletePlayer, handleBulkDeletePlayers, togglePlayerSelection, handleSelectAllPlayers, reloadPlayers }}
              />
            )}
            {view === "Tournaments" && <TournamentsTab tournaments={tournaments} />}
            {view === "Stats" && <StatsTab stats={stats} />}
          </>
        )}
      </div>
    </DashboardLayout>
  );
}
