// src/components/Tournament/SportPicker.js
import React, { useState } from "react";
import { sportsList } from "./sportsList";

/**
 * SportPicker
 * Props:
 * - onPick: (sportObj) => void
 */
export default function SportPicker({ onPick }) {
  const [query, setQuery] = useState("");
  const filtered = sportsList.filter(s =>
    s.label.toLowerCase().includes(query.toLowerCase())
  );
  return (
    <div>
      <input
        type="text"
        placeholder="Search sportsâ€¦"
        value={query}
        onChange={e => setQuery(e.target.value)}
        className="border p-2 rounded w-full mb-2"
      />
      <div className="flex flex-wrap gap-2 max-h-32 overflow-y-auto">
        {filtered.map(s => (
          <button
            key={s.key}
            className="bg-athletiq-navy text-white rounded-lg px-3 py-2 flex flex-col items-center shadow hover:bg-athletiq-blue transition"
            onClick={() => onPick({ ...s })}
            type="button"
            style={{ minWidth: 70 }}
          >
            <span className="text-2xl">{s.icon}</span>
            <span className="text-xs mt-1">{s.label}</span>
          </button>
        ))}
        {filtered.length === 0 && <div className="text-xs text-gray-400">No match</div>}
      </div>
    </div>
  );
}
