import React from "react";
import { useTranslation } from "react-i18next";

export default function StepAdminInfo({ data, updateData, next, back }) {
  const { t } = useTranslation();

  return (
    <div>
      <h2 className="text-xl font-bold mb-4">{t("admin_info")}</h2>
      <input
        className="w-full border p-2 mb-2"
        placeholder={t("admin_name")}
        value={data.adminName || ""}
        onChange={e => updateData({ adminName: e.target.value })}
        required
      />
      <input
        className="w-full border p-2 mb-2"
        placeholder={t("admin_email")}
        type="email"
        value={data.adminEmail || ""}
        onChange={e => updateData({ adminEmail: e.target.value })}
        required
      />
      <input
        className="w-full border p-2 mb-2"
        placeholder={t("admin_password")}
        type="password"
        value={data.adminPassword || ""}
        onChange={e => updateData({ adminPassword: e.target.value })}
        required
      />
      <div className="flex justify-between mt-4">
        <button onClick={back} className="text-gray-500">
          {t("back")}
        </button>
        <button
          onClick={next}
          className="bg-blue-500 text-white px-4 py-2 rounded"
          disabled={
            !data.adminName || !data.adminEmail || !data.adminPassword
          }
        >
          {t("next")}
        </button>
      </div>
    </div>
  );
}
