import React, { useState } from "react";
import { AnimatePresence, motion } from "framer-motion";
import StepWelcome from "../components/registration/steps/StepWelcome";
import SchoolAddressGoogle from "../components/registration/steps/SchoolAddressGoogle";
import StepSchoolType from "../components/registration/steps/StepSchoolType";
import StepContactInfo from "../components/registration/steps/StepContactInfo";
import StepUploads from "../components/registration/steps/StepUploads";
import StepAdminInfo from "../components/registration/steps/StepAdminInfo";
import StepReviewSubmit from "../components/registration/steps/StepReviewSubmit";
import StepSuccess from "../components/registration/steps/StepSuccess";

const steps = [
  StepWelcome,
  SchoolAddressGoogle,
  StepSchoolType,
  StepContactInfo,
  StepUploads,
  StepAdminInfo,
  StepReviewSubmit,
  StepSuccess
];

export default function SchoolRegistrationWizard() {
  const [step, setStep] = useState(0);
  const [data, setData] = useState({
    province: "",
    district: "",
    municipality: "",
    address: "",
  country: "",
  state: "",
  city: "",
  postalCode: "",
  lat: "",
  lng: "",
  schoolType: "", 
    ward: "",
    tole: "",
    mobile: "",
    landline: "",
    email: "",
    schoolLogo: null,
    officialDoc: null,
    adminName: "",
    adminEmail: "",
    adminPassword: ""
  });

  // handle submit (StepReviewSubmit will call this)
  const handleSubmit = async () => {
    // TODO: send data to backend here (e.g., as FormData)
    setStep(steps.length - 1); // Go to StepSuccess
  };

  const StepComponent = steps[step];

  return (
    <div className="max-w-md mx-auto mt-10 bg-white shadow-xl rounded-2xl p-8">
      <AnimatePresence mode="wait" initial={false}>
        <motion.div
          key={step}
          initial={{ opacity: 0, x: 40 }}
          animate={{ opacity: 1, x: 0 }}
          exit={{ opacity: 0, x: -40 }}
          transition={{ duration: 0.4 }}
        >
          <StepComponent
            data={data}
            updateData={updates => setData(prev => ({ ...prev, ...updates }))}
            next={() => setStep(step + 1)}
            back={() => setStep(Math.max(0, step - 1))}
            onSubmit={handleSubmit}
          />
        </motion.div>
      </AnimatePresence>
    </div>
  );
}
