// src/pages/TournamentSetup.jsx

import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import axios from "axios";

import StepSports from "../components/Tournament/StepSports";
import StepSportDetails from "../components/Tournament/StepSportDetails";
import StepMatchFormat from "../components/Tournament/StepMatchFormat";
import BracketPreview from "../components/Tournament/BracketPreview";
import ConfirmTournament from "../components/Tournament/ConfirmTournament";

const steps = [
  "Select Sports",
  "Configure Details",
  "Match Format",
  "Bracket Preview",
  "Confirm Setup"
];

export default function TournamentSetup() {
  const { tournamentId } = useParams();
  const navigate = useNavigate();
  const [activeStep, setActiveStep] = useState(0);
  const [tournament, setTournament] = useState(null);
  const [error, setError] = useState(null);

  const token = localStorage.getItem("token");

  useEffect(() => {
    if (!tournamentId) return;
    const fetchTournament = async () => {
      try {
        const res = await axios.get(
          `http://localhost:5000/api/tournaments/${tournamentId}`,
          {
            headers: { Authorization: `Bearer ${token}` }
          }
        );
        setTournament(res.data);
      } catch (err) {
        console.error(err);
        setError("Failed to load tournament data. Please try again.");
      }
    };
    fetchTournament();
  }, [tournamentId, token]);

  function handleNext(data = {}) {
    setTournament((prev) => ({ ...prev, ...data }));
    setActiveStep((prev) => prev + 1);
  }

  function handleBack() {
    setActiveStep((prev) => Math.max(prev - 1, 0));
  }

  function renderStepComponent() {
    switch (activeStep) {
      case 0:
        return <StepSports data={tournament} onNext={handleNext} />;
      case 1:
        return <StepSportDetails data={tournament} onNext={handleNext} onBack={handleBack} />;
      case 2:
        return <StepMatchFormat data={tournament} onNext={handleNext} onBack={handleBack} />;
      case 3:
        return <BracketPreview data={tournament} onNext={handleNext} onBack={handleBack} />;
      case 4:
        return <ConfirmTournament data={tournament} onBack={handleBack} navigate={navigate} />;
      default:
        return null;
    }
  }

  return (
    <div className="max-w-5xl mx-auto p-6">
      <h1 className="text-2xl font-bold mb-4">Tournament Setup</h1>
      <div className="mb-4 text-gray-600">Step {activeStep + 1} of {steps.length}: {steps[activeStep]}</div>
      {error ? <div className="text-red-500">{error}</div> : renderStepComponent()}
    </div>
  );
}
