import React from "react";
import { useNavigate } from "react-router-dom";

export default function Home() {
  const navigate = useNavigate();

  const token = localStorage.getItem("token");

  // Optional: If not logged in, redirect to login
  React.useEffect(() => {
    if (!token) {
      navigate("/login");
    }
  }, [navigate, token]);

  return (
    <div className="min-h-screen flex flex-col items-center justify-center bg-gradient-to-br from-blue-50 to-green-50">
      <div className="bg-white p-8 rounded-2xl shadow-md w-full max-w-lg text-center">
        <h1 className="text-3xl font-bold text-blue-700 mb-6">Welcome to ATHLETIQ</h1>
        <p className="mb-6 text-gray-600">
          Please select an action:
        </p>
        <div className="flex flex-col gap-4">
          <button
            className="px-6 py-3 bg-green-600 text-white rounded-xl font-semibold hover:bg-green-700 transition"
            onClick={() => navigate("/register-player")}
          >
            + Register Player
          </button>
          <button
            className="px-6 py-3 bg-blue-600 text-white rounded-xl font-semibold hover:bg-blue-700 transition"
            onClick={() => navigate("/dashboard")}
          >
            View School Dashboard
          </button>
          <button
            className="px-6 py-3 bg-gray-300 text-gray-800 rounded-xl font-semibold hover:bg-gray-400 transition"
            onClick={() => {
              localStorage.removeItem("token");
              navigate("/login");
            }}
          >
            Logout
          </button>
        </div>
      </div>
    </div>
  );
}
