// src/components/AdminDashboard/TournamentsTab.js
import React, { useState } from "react";
import axios from "axios";
import AddTournamentModal from "./AddTournamentModal";
import AddSportModal from "../Tournament/AddSportModal";
import EditSportModal from "../Tournament/EditSportModal";

// Helper to fetch sports for a tournament
async function fetchTournamentSports(tournamentId, token) {
  const res = await axios.get(
    `http://localhost:5000/api/tournaments/${tournamentId}/sports`,
    { headers: { Authorization: `Bearer ${token}` } }
  );
  return res.data || [];
}

/**
 * Props:
 *   tournaments: array,
 *   user: object,
 *   reloadTournaments: function (should refetch tournaments from backend after add)
 */
export default function TournamentsTab({ tournaments, user, reloadTournaments }) {
  const [expandedId, setExpandedId] = useState(null);
  const [sports, setSports] = useState([]);
  const [addOpen, setAddOpen] = useState(false); // Add Sport
  const [editSport, setEditSport] = useState(null);
  const [addTournamentOpen, setAddTournamentOpen] = useState(false); // Add Tournament
  const [selectedTournament, setSelectedTournament] = useState(null);
  const [loadingSports, setLoadingSports] = useState(false);
  const token = localStorage.getItem("token");

  // Expand/collapse and load sports for a tournament
  const handleExpand = async (t) => {
    if (expandedId === t.id) {
      setExpandedId(null);
      setSports([]);
      return;
    }
    setExpandedId(t.id);
    setLoadingSports(true);
    setSelectedTournament(t);
    setSports(await fetchTournamentSports(t.id, token));
    setLoadingSports(false);
  };

  // Reload sports after add/edit/delete
  const reloadSports = async () => {
    if (expandedId) {
      setLoadingSports(true);
      setSports(await fetchTournamentSports(expandedId, token));
      setLoadingSports(false);
    }
  };

  // Handle delete sport
  const handleDeleteSport = async (sportId) => {
    if (!window.confirm("Delete this sport from the tournament?")) return;
    await axios.delete(
      `http://localhost:5000/api/tournaments/${expandedId}/sports/${sportId}`,
      { headers: { Authorization: `Bearer ${token}` } }
    );
    reloadSports();
  };

  // Add tournament then reload list
  const handleTournamentAdded = () => {
    setAddTournamentOpen(false);
    reloadTournaments && reloadTournaments();
  };

  return (
    <div>
      {/* Add Tournament Modal */}
      <AddTournamentModal
        open={addTournamentOpen}
        onClose={() => setAddTournamentOpen(false)}
        user={user}
        onAdded={handleTournamentAdded}
      />

      <div className="flex items-center justify-between mb-4">
        <h2 className="text-xl font-bold text-athletiq-navy">All Tournaments</h2>
        {user && (
          <button
            className="flex items-center gap-2 px-4 py-2 rounded bg-athletiq-green text-white font-semibold hover:bg-green-700 shadow"
            onClick={() => setAddTournamentOpen(true)}
          >
            <span>ï¼‹</span>
            <span>Add Tournament</span>
          </button>
        )}
      </div>
      {tournaments.length === 0 ? (
        <div>No tournaments found.</div>
      ) : (
        <div className="overflow-x-auto bg-white rounded-lg shadow-md">
          <table className="min-w-full text-sm">
            <thead>
              <tr className="bg-athletiq-blue text-white">
                <th></th>
                <th className="p-2 text-left">Name</th>
                <th className="p-2 text-left">Code</th>
                <th className="p-2 text-left">Sports</th>
                <th className="p-2 text-left">Start Date</th>
                <th className="p-2 text-left">Status</th>
                <th className="p-2 text-left">Actions</th>
              </tr>
            </thead>
            <tbody>
              {tournaments.map((t) => (
                <React.Fragment key={t.id}>
                  <tr className="border-b">
                    <td className="p-2">
                      <button
                        onClick={() => handleExpand(t)}
                        className="text-athletiq-blue font-bold text-xl"
                        title={expandedId === t.id ? "Collapse" : "Expand"}
                      >
                        {expandedId === t.id ? "âˆ’" : "+"}
                      </button>
                    </td>
                    <td className="p-2">{t.name}</td>
                    <td className="p-2">{t.tournament_code}</td>
                    <td className="p-2">
                      {Array.isArray(t.sports)
                        ? t.sports.join(", ")
                        : JSON.parse(t.sports || "[]").join(", ")}
                    </td>
                    <td className="p-2">{t.start_date?.slice(0, 10) || "-"}</td>
                    <td className="p-2">{t.status || "Active"}</td>
                    <td className="p-2 flex gap-2">{/* Edit/Delete tournament here if needed */}</td>
                  </tr>
                  {/* Expandable row */}
                  {expandedId === t.id && (
                    <tr>
                      <td colSpan={7} className="bg-gray-50 px-8 pb-6">
                        <div className="py-4">
                          <div className="flex justify-between items-center mb-2">
                            <span className="text-lg font-semibold text-athletiq-navy">Sports in this Tournament</span>
                            <button
                              className="px-4 py-2 bg-athletiq-green text-white rounded"
                              onClick={() => setAddOpen(true)}
                            >
                              + Add Sport
                            </button>
                          </div>
                          {loadingSports ? (
                            <div>Loading sports...</div>
                          ) : sports.length === 0 ? (
                            <div>No sports added yet.</div>
                          ) : (
                            <table className="min-w-full text-xs border">
                              <thead>
                                <tr className="bg-athletiq-blue text-white">
                                  <th className="p-2 text-left">Sport Name</th>
                                  <th className="p-2 text-left">Team Size</th>
                                  <th className="p-2 text-left">Max Teams</th>
                                  <th className="p-2 text-left">Rules</th>
                                  <th className="p-2 text-left">Actions</th>
                                </tr>
                              </thead>
                              <tbody>
                                {sports.map((s) => (
                                  <tr key={s.id} className="border-b">
                                    <td className="p-2">{s.sport_name}</td>
                                    <td className="p-2">{s.team_size || "-"}</td>
                                    <td className="p-2">{s.max_teams || "-"}</td>
                                    <td className="p-2">
                                      {s.rules_url ? (
                                        <a
                                          href={s.rules_url}
                                          target="_blank"
                                          rel="noopener noreferrer"
                                          className="text-athletiq-blue underline"
                                        >
                                          View
                                        </a>
                                      ) : (
                                        "-"
                                      )}
                                    </td>
                                    <td className="p-2 flex gap-2">
                                      <button
                                        className="px-2 py-1 bg-athletiq-blue text-white rounded"
                                        onClick={() => setEditSport(s)}
                                      >
                                        Edit
                                      </button>
                                      <button
                                        className="px-2 py-1 bg-red-500 text-white rounded"
                                        onClick={() => handleDeleteSport(s.id)}
                                      >
                                        Delete
                                      </button>
                                    </td>
                                  </tr>
                                ))}
                              </tbody>
                            </table>
                          )}
                        </div>
                        {/* Add Sport Modal */}
                        <AddSportModal
                          open={addOpen}
                          onClose={() => setAddOpen(false)}
                          tournamentId={t.id}
                          onAdded={reloadSports}
                        />
                        {/* Edit Sport Modal */}
                        {editSport && (
                          <EditSportModal
                            open={!!editSport}
                            sport={editSport}
                            tournamentId={t.id}
                            onClose={() => setEditSport(null)}
                            onUpdated={reloadSports}
                          />
                        )}
                      </td>
                    </tr>
                  )}
                </React.Fragment>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
}
