import React from "react";
import { useTranslation } from "react-i18next";

export default function StepContactInfo({ data, updateData, next, back }) {
  const { t } = useTranslation();

  return (
    <div>
      <h2 className="text-xl font-bold mb-4">{t("contact_info")}</h2>
      <input
        className="w-full border p-2 mb-2"
        placeholder={t("mobile")}
        value={data.mobile || ""}
        onChange={e => updateData({ mobile: e.target.value })}
        required
      />
      <input
        className="w-full border p-2 mb-2"
        placeholder={t("landline")}
        value={data.landline || ""}
        onChange={e => updateData({ landline: e.target.value })}
      />
      <input
        className="w-full border p-2 mb-2"
        placeholder={t("email")}
        value={data.email || ""}
        onChange={e => updateData({ email: e.target.value })}
        required
      />
      <div className="flex justify-between mt-4">
        <button onClick={back} className="text-gray-500">
          {t("back")}
        </button>
        <button
          onClick={next}
          className="bg-blue-500 text-white px-4 py-2 rounded"
          disabled={!data.mobile || !data.email}
        >
          {t("next")}
        </button>
      </div>
    </div>
  );
}
