// src/App.js

import React from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";

// Import your dashboard/pages
import Login from "./pages/Login";
import SchoolDashboard from "./pages/SchoolDashboard";
import SuperAdminDashboard from "./pages/AdminDashboard";
import PlayerDashboard from "./pages/PlayerDashboard";
import CoachDashboard from "./pages/CoachDashboard";
import RefereeDashboard from "./pages/RefereeDashboard";
import OrgDashboard from "./pages/OrgDashboard";
 // <-- Import Tournament Page
import TournamentsPage from "./pages/TournamentsPage";
// Utility: Get user role from localStorage
function getUserRole() {
  try {
    const user = JSON.parse(localStorage.getItem("user"));
    return user?.role || null;
  } catch (error) {
    console.error("Error fetching user role:", error);
    return null;
  }
}

// Simple role-based private route
function PrivateRoute({ children, role }) {
  const userRole = getUserRole();
  if (!userRole) return <Navigate to="/login" replace />;
  if (role && userRole !== role) return <Navigate to="/login" replace />;
  return children;
}

/**
 * Main ATHLETIQ App Routing
 * 
 * ONBOARDING NOTES:
 * - All dashboard pages are split by user role.
 * - Use the PrivateRoute component to protect dashboards.
 * - Tournament page is only accessible to super_admin for now.
 * - To add more routes, just import the page and add a <Route /> below.
 */
function App() {
  return (
    <Router>
      <Routes>
        {/* Universal Login */}
        <Route path="/login" element={<Login />} />

        {/* Super Admin Dashboard */}
        <Route
          path="/admin"
          element={
            <PrivateRoute role="super_admin">
              <SuperAdminDashboard />
            </PrivateRoute>
          }
        />

        {/* Super Admin Tournaments Page */}

<Route path="/admin/tournaments" element={<TournamentsPage />} />
        {/* School Admin Dashboard */}
        <Route
          path="/school-dashboard"
          element={
            <PrivateRoute role="school_admin">
              <SchoolDashboard />
            </PrivateRoute>
          }
        />

        {/* Player Dashboard */}
        <Route
          path="/player-dashboard"
          element={
            <PrivateRoute role="player">
              <PlayerDashboard />
            </PrivateRoute>
          }
        />

        {/* Coach Dashboard */}
        <Route
          path="/coach-dashboard"
          element={
            <PrivateRoute role="coach">
              <CoachDashboard />
            </PrivateRoute>
          }
        />

        {/* Referee Dashboard */}
        <Route
          path="/referee-dashboard"
          element={
            <PrivateRoute role="referee">
              <RefereeDashboard />
            </PrivateRoute>
          }
        />

        {/* Organization Dashboard */}
        <Route
          path="/org-dashboard"
          element={
            <PrivateRoute role="organization">
              <OrgDashboard />
            </PrivateRoute>
          }
        />

        {/* Default route: redirect to login */}
        <Route path="*" element={<Navigate to="/login" replace />} />
      </Routes>
    </Router>
  );
}

export default App;
