import React, { useState } from "react";
import axios from "axios";

export default function AddPlayerModal({ open, onClose, onAdded, schools }) {
  const user = JSON.parse(localStorage.getItem("user") || "{}");
  const isSchoolAdmin = user.role === "school_admin";
  const defaultSchoolId = isSchoolAdmin ? user.school_id : (schools[0]?.id || "");

  const [form, setForm] = useState({
    full_name: "",
    full_name_nep: "",
    dob: "",
    school_id: defaultSchoolId,
    gender: "",
    guardian_name: "",
    guardian_phone: "",
    district: "",
  });
  const [loading, setLoading] = useState(false);
  const [err, setErr] = useState("");
  const [photoFile, setPhotoFile] = useState(null);
  const [certFile, setCertFile] = useState(null);

  if (!open) return null;

  const handleChange = (e) => {
    const { name, value } = e.target;
    setForm((f) => ({ ...f, [name]: value }));
  };

  const handlePhoto = (e) => {
    setPhotoFile(e.target.files[0]);
  };

  const handleCert = (e) => {
    setCertFile(e.target.files[0]);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setErr("");
    try {
      const data = new FormData();
      data.append("full_name", form.full_name);
      data.append("full_name_nep", form.full_name_nep);
      data.append("dob", form.dob);
      data.append("gender", form.gender);
      data.append("guardian_name", form.guardian_name);
      data.append("guardian_phone", form.guardian_phone);
      data.append("district", form.district);

      if (!isSchoolAdmin) {
        data.append("school_id", form.school_id);
      }

      if (photoFile) data.append("photo", photoFile);
      if (certFile) data.append("birth_cert", certFile);

      const token = localStorage.getItem("token");
      await axios.post(
        "http://localhost:5000/api/players/register",
        data,
        { headers: { Authorization: `Bearer ${token}` } }
      );
      onAdded && onAdded();
      onClose();
      setForm({
        full_name: "",
        full_name_nep: "",
        dob: "",
        school_id: defaultSchoolId,
        gender: "",
        guardian_name: "",
        guardian_phone: "",
        district: "",
      });
      setPhotoFile(null);
      setCertFile(null);
    } catch (error) {
      setErr(error?.response?.data?.message || "Registration failed.");
    }
    setLoading(false);
  };

  return (
    <div className="fixed inset-0 flex items-center justify-center z-50 bg-black bg-opacity-30">
      <div className="bg-white p-6 rounded shadow-lg w-full max-w-lg relative overflow-y-auto" style={{ maxHeight: "90vh" }}>
        <button className="absolute top-2 right-3 text-lg" onClick={onClose} type="button">
          &times;
        </button>
        <h2 className="text-xl font-bold mb-4">Add Player</h2>
        <form onSubmit={handleSubmit} className="space-y-3" encType="multipart/form-data">
          <div>
            <label className="block text-sm mb-1">Full Name</label>
            <input
              className="w-full border p-2 rounded"
              name="full_name"
              value={form.full_name}
              onChange={handleChange}
              required
            />
          </div>
          <div>
            <label className="block text-sm mb-1">рдирд╛рдо (Nepali)</label>
            <input
              className="w-full border p-2 rounded"
              name="full_name_nep"
              value={form.full_name_nep}
              onChange={handleChange}
            />
          </div>
          <div>
            <label className="block text-sm mb-1">Date of Birth</label>
            <input
              className="w-full border p-2 rounded"
              type="date"
              name="dob"
              value={form.dob}
              onChange={handleChange}
              required
            />
          </div>
          {!isSchoolAdmin && (
            <div>
              <label className="block text-sm mb-1">School</label>
              <select
                className="w-full border p-2 rounded"
                name="school_id"
                value={form.school_id}
                onChange={handleChange}
                required
              >
                <option value="">Select school...</option>
                {schools.map((s) => (
                  <option key={s.id} value={s.id}>{s.name}</option>
                ))}
              </select>
            </div>
          )}
          <div>
            <label className="block text-sm mb-1">Gender</label>
            <select
              className="w-full border p-2 rounded"
              name="gender"
              value={form.gender}
              onChange={handleChange}
              required
            >
              <option value="">Select gender...</option>
              <option value="M">Male</option>
              <option value="F">Female</option>
              <option value="O">Other</option>
            </select>
          </div>
          <div>
            <label className="block text-sm mb-1">Guardian Name</label>
            <input
              className="w-full border p-2 rounded"
              name="guardian_name"
              value={form.guardian_name}
              onChange={handleChange}
            />
          </div>
          <div>
            <label className="block text-sm mb-1">Guardian Phone</label>
            <input
              className="w-full border p-2 rounded"
              name="guardian_phone"
              value={form.guardian_phone}
              onChange={handleChange}
            />
          </div>
          <div>
            <label className="block text-sm mb-1">District</label>
            <input
              className="w-full border p-2 rounded"
              name="district"
              value={form.district}
              onChange={handleChange}
            />
          </div>
          <div>
            <label className="block text-sm mb-1">Profile Photo</label>
            <input
              type="file"
              onChange={handlePhoto}
              accept="image/*"
            />
          </div>
          <div>
            <label className="block text-sm mb-1">Birth Certificate</label>
            <input
              type="file"
              onChange={handleCert}
              accept="image/*,application/pdf"
            />
          </div>
          {err && <div className="text-red-500">{err}</div>}
          <button
            type="submit"
            disabled={loading}
            className="w-full bg-blue-600 text-white rounded py-2 mt-2"
          >
            {loading ? "Registering..." : "Register Player"}
          </button>
        </form>
      </div>
    </div>
  );
}
