// src/components/DashboardLayout.js

import React from "react";
import Sidebar from "./Sidebar";

// Use props: school (object), user (object), onLogout (function), children (content)
export default function DashboardLayout({ school, user, onLogout, children }) {
  return (
    <div className="flex bg-athletiq-cream min-h-screen">
      <Sidebar onLogout={onLogout} />
      <div className="flex-1 flex flex-col">
        {/* Header */}
        <header className="bg-athletiq-blue text-white px-8 py-4 flex items-center justify-between shadow">
          <div className="text-xl font-semibold">School Dashboard</div>
          <div className="flex items-center gap-3">
            <div className="text-base font-bold">
              {school?.name || "School Name"}
            </div>
            {/* School logo or avatar */}
            {school?.logo_url ? (
              <img
                src={`/uploads/${school.logo_url}`}
                alt="School"
                className="h-8 w-8 rounded-full border-2 border-athletiq-green"
              />
            ) : (
              <div className="h-8 w-8 rounded-full bg-athletiq-green flex items-center justify-center text-athletiq-navy font-bold">
                {school?.name?.charAt(0) || "S"}
              </div>
            )}
          </div>
        </header>
        {/* Main Content */}
        <main className="flex-1 px-8 py-6">{children}</main>
      </div>
    </div>
  );
}
