import React, { useState } from "react";
import { AnimatePresence, motion } from "framer-motion";
import { useLang } from "../contexts/LangContext";
import StepWelcome from "../components/registration/steps/StepWelcome";
import StepTypeAndLocation from "../components/registration/steps/StepTypeAndLocation";
import StepContactInfo from "../components/registration/steps/StepContactInfo";

// Import next steps as you build...

const steps = [
  StepWelcome,
  StepTypeAndLocation,
  // ...add more steps here
];

export default function SchoolRegistrationWizard() {
  const [step, setStep] = useState(0);
  const { lang, setLang } = useLang();
  const [data, setData] = useState({
    schoolType: "",
    province: "",
    district: "",
    municipality: "",
    ward: "",
    tole: ""
    // Add more fields as you add steps!
  });

  // Progress Dots
  const ProgressDots = () => (
    <div className="flex gap-2 justify-center mb-6">
      {steps.map((_, i) => (
        <span
          key={i}
          className={`inline-block w-3 h-3 rounded-full ${
            i === step ? "bg-blue-500" : "bg-gray-300"
          }`}
        ></span>
      ))}
    </div>
  );

  // Language toggle at top right
  const LangToggle = () => (
    <div className="flex justify-end mb-4">
      <button
        className={`mr-2 ${lang === "en" ? "font-bold text-blue-500" : ""}`}
        onClick={() => setLang("en")}
      >
        EN
      </button>
      <button
        className={`${lang === "np" ? "font-bold text-green-600" : ""}`}
        onClick={() => setLang("np")}
      >
        рдиреЗ
      </button>
    </div>
  );

  const StepComponent = steps[step];

  return (
    <div className="max-w-md mx-auto mt-10 bg-white shadow-xl rounded-2xl p-8">
      <LangToggle />
      <ProgressDots />
      <AnimatePresence mode="wait" initial={false}>
        <motion.div
          key={step}
          initial={{ opacity: 0, x: 40 }}
          animate={{ opacity: 1, x: 0 }}
          exit={{ opacity: 0, x: -40 }}
          transition={{ duration: 0.4 }}
        >
          <StepComponent
            data={data}
            updateData={updates => setData(prev => ({ ...prev, ...updates }))}
            next={() => setStep(step + 1)}
            back={() => setStep(Math.max(0, step - 1))}
          />
        </motion.div>
      </AnimatePresence>
    </div>
  );
}
