// src/components/Tournament/ConfirmTournament.jsx

import React from "react";
import BracketPreview from "./BracketPreview";
import { Card, CardContent } from "../ui/card";
import { Button } from "../ui/button";
import { CheckCircle, Pencil } from "lucide-react";

export default function ConfirmTournament({ sports, onConfirm, onEdit }) {
  return (
    <div className="w-full">
      <h2 className="text-xl font-bold text-athletiq-navy mb-6">
        Final Review & Confirmation
      </h2>

      <div className="flex flex-col gap-6">
        {sports.map((sport) => (
          <Card
            key={sport.id}
            className="border border-athletiq-blue bg-athletiq-cream rounded-xl"
          >
            <CardContent className="p-4">
              <div className="flex justify-between items-center mb-3">
                <div>
                  <h3 className="font-bold text-athletiq-navy text-lg">
                    {sport.name} â€“ {sport.gender} / {sport.ageGroup}
                  </h3>
                  <p className="text-sm text-athletiq-navy">
                    Format: {sport.format} | Team Size: {sport.teamSize} | Total Teams: {sport.totalTeams}
                  </p>
                </div>
                <button
                  onClick={() => onEdit(sport.id)}
                  className="text-athletiq-blue flex items-center gap-1 hover:underline"
                >
                  <Pencil size={16} /> Edit
                </button>
              </div>

              <BracketPreview sports={[sport]} />
            </CardContent>
          </Card>
        ))}
      </div>

      <div className="flex justify-end mt-8">
        <Button
          className="bg-athletiq-green text-white hover:bg-green-700 px-6 py-2 text-md font-semibold rounded"
          onClick={onConfirm}
        >
          <CheckCircle className="w-5 h-5 mr-2 inline-block" /> Confirm Tournament Setup
        </Button>
      </div>
    </div>
  );
}
