import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import axios from "axios";

export default function PlayerProfile() {
  const { id } = useParams(); // grabs :id from URL
  const navigate = useNavigate();
  const [player, setPlayer] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const token = localStorage.getItem("token");

  useEffect(() => {
    if (!token) {
      navigate("/login");
      return;
    }

    const fetchPlayer = async () => {
      try {
        const res = await axios.get(
          `http://localhost:5000/api/players/${id}`,
          { headers: { Authorization: `Bearer ${token}` } }
        );
        setPlayer(res.data);
      } catch (err) {
        setError("Player not found or unauthorized.");
      } finally {
        setLoading(false);
      }
    };

    fetchPlayer();
  }, [id, token, navigate]);

  if (loading) {
    return <div className="min-h-screen flex items-center justify-center text-xl">Loading player...</div>;
  }

  if (error) {
    return <div className="min-h-screen flex items-center justify-center text-red-600 text-xl">{error}</div>;
  }

  if (!player) return null;

  return (
    <div className="min-h-screen flex items-center justify-center bg-blue-50">
      <div className="bg-white p-8 rounded-xl shadow w-full max-w-lg">
        <button
          className="mb-4 px-4 py-2 bg-gray-300 text-gray-700 rounded hover:bg-gray-400"
          onClick={() => navigate(-1)}
        >
          ‚Üê Back
        </button>
        <h2 className="text-2xl font-bold mb-2 text-blue-700">{player.full_name_eng || player.name}</h2>
        <div className="mb-4 text-gray-600">{player.full_name_nep}</div>
        <div className="flex gap-6 mb-4">
          <div>
            <strong>Date of Birth (AD):</strong> {player.dob_ad || player.dob}
          </div>
          <div>
            <strong>Date of Birth (BS):</strong> {player.dob_bs}
          </div>
        </div>
        <div className="mb-2"><strong>Gender:</strong> {player.gender}</div>
        <div className="mb-2"><strong>Father's Name:</strong> {player.father_name}</div>
        <div className="mb-2"><strong>Mother's Name:</strong> {player.mother_name}</div>
        <div className="mb-2"><strong>Status:</strong> {player.status || "approved"}</div>
        <div className="flex gap-6 mt-4">
          {player.photo && (
            <div>
              <div className="font-semibold text-gray-500">Photo</div>
              <img
                src={`/uploads/${player.photo}`}
                alt="Player"
                className="w-28 h-28 object-cover border rounded-lg mt-1"
              />
            </div>
          )}
          {player.certificate && (
            <div>
              <div className="font-semibold text-gray-500">Birth Certificate</div>
              <img
                src={`/uploads/${player.certificate}`}
                alt="Birth Certificate"
                className="w-28 h-28 object-cover border rounded-lg mt-1"
              />
            </div>
          )}
        </div>
      </div>
    </div>
  );
}
