import React, { useState, useEffect, useRef } from "react";
import axios from "axios";

export default function AddPlayerModal({ open, onClose, onAdded, schools }) {
  const [form, setForm] = useState({
    full_name: "",
    dob: "",
    school_id: schools[0]?.id || "",
    gender: "",
    main_sport: "",
    guardian_phone: "",
  });
  const [photoFile, setPhotoFile] = useState(null);
  const [certFile, setCertFile] = useState(null);
  const [loading, setLoading] = useState(false);
  const [err, setErr] = useState("");

  useEffect(() => {
    if (open) {
      setForm({
        full_name: "",
        dob: "",
        school_id: schools[0]?.id || "",
        gender: "",
        main_sport: "",
        guardian_phone: "",
      });
      setPhotoFile(null);
      setCertFile(null);
      setErr("");
    }
  }, [open, schools]);

  if (!open) return null;

  const handleChange = (e) => {
    setForm((f) => ({ ...f, [e.target.name]: e.target.value }));
  };
  const handlePhoto = (e) => setPhotoFile(e.target.files[0]);
  const handleCert = (e) => setCertFile(e.target.files[0]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setErr("");

    // Simple frontend validation
    if (
      !form.full_name ||
      !form.dob ||
      !form.school_id ||
      !form.gender ||
      !form.main_sport
    ) {
      setErr("Please fill all required fields.");
      setLoading(false);
      return;
    }

    try {
      const data = new FormData();
      Object.entries(form).forEach(([k, v]) => {
        if (v) data.append(k, v);
      });
      if (photoFile) data.append("photo", photoFile);
      if (certFile) data.append("birth_cert", certFile);

      const token = localStorage.getItem("token");
      const res = await axios.post(
        "http://localhost:5000/api/players/register",
        data,
        {
          headers: {
            Authorization: `Bearer ${token}`,
            "Content-Type": "multipart/form-data",
          },
        }
      );
      onAdded(res.data);
      onClose();
    } catch (error) {
      setErr(error?.response?.data?.message || "Add player failed.");
    }
    setLoading(false);
  };

  return (
    <div className="fixed inset-0 flex items-center justify-center z-50 bg-black bg-opacity-30">
      <div className="bg-white p-6 rounded shadow-lg w-full max-w-lg relative">
        <button
          className="absolute top-2 right-3 text-lg"
          onClick={onClose}
          type="button"
        >
          &times;
        </button>
        <h2 className="text-xl font-bold mb-4">Add Player</h2>
        <form onSubmit={handleSubmit} className="space-y-3">
          <div>
            <label className="block text-sm mb-1">Full Name *</label>
            <input
              name="full_name"
              value={form.full_name}
              onChange={handleChange}
              required
              className="w-full border p-2 rounded"
            />
          </div>
          <div>
            <label className="block text-sm mb-1">Date of Birth *</label>
            <input
              type="date"
              name="dob"
              value={form.dob}
              onChange={handleChange}
              required
              className="w-full border p-2 rounded"
            />
          </div>
          <div>
            <label className="block text-sm mb-1">School *</label>
            <select
              name="school_id"
              value={form.school_id}
              onChange={handleChange}
              required
              className="w-full border p-2 rounded"
            >
              {schools.map((s) => (
                <option key={s.id} value={s.id}>
                  {s.name}
                </option>
              ))}
            </select>
          </div>
          <div>
            <label className="block text-sm mb-1">Gender *</label>
            <select
              name="gender"
              value={form.gender}
              onChange={handleChange}
              required
              className="w-full border p-2 rounded"
            >
              <option value="">Select...</option>
              <option value="M">Male</option>
              <option value="F">Female</option>
              <option value="O">Other</option>
            </select>
          </div>
          <div>
            <label className="block text-sm mb-1">Main Sport *</label>
            <input
              name="main_sport"
              value={form.main_sport}
              onChange={handleChange}
              required
              placeholder="e.g. Football"
              className="w-full border p-2 rounded"
            />
          </div>
          <div>
            <label className="block text-sm mb-1">Guardian Phone</label>
            <input
              name="guardian_phone"
              value={form.guardian_phone}
              onChange={handleChange}
              className="w-full border p-2 rounded"
            />
          </div>
          <div className="flex gap-4 items-center">
            <div>
              <label className="block text-sm mb-1">Photo</label>
              <input type="file" accept="image/*" onChange={handlePhoto} />
            </div>
            <div>
              <label className="block text-sm mb-1">Birth Certificate</label>
              <input
                type="file"
                accept="image/*,application/pdf"
                onChange={handleCert}
              />
            </div>
          </div>
          {err && <div className="text-red-500">{err}</div>}
          <button
            type="submit"
            disabled={loading}
            className="w-full bg-blue-600 text-white rounded py-2 mt-2"
          >
            {loading ? "Adding..." : "Add Player"}
          </button>
        </form>
      </div>
    </div>
  );
}
