// src/components/Sidebar.js

import React from "react";
import { useLocation } from "react-router-dom";
import {
  Home,
  Users,
  UserPlus,
  Shield,
  Award,
  Briefcase,
  BarChart,
  Settings,
  LifeBuoy,
  LogOut,
  CreditCard,
  School,
  Trophy,
  Layers,
  UserCheck
} from "lucide-react";

/**
 * All sidebar links by role.
 * Each link: { label, icon, to, roles }
 */
const SIDEBAR_LINKS = [
  // Home/dashboard for all
  { label: "Dashboard", icon: <Home size={20} />, to: "/dashboard", roles: ["super_admin", "school_admin", "player", "coach", "referee", "organization"] },

  // Super admin features
  { label: "Schools", icon: <School size={20} />, to: "/admin/schools", roles: ["super_admin"] },
  { label: "Players", icon: <Users size={20} />, to: "/admin/players", roles: ["super_admin"] },
  { label: "Tournaments", icon: <Trophy size={20} />, to: "/admin/tournaments", roles: ["super_admin"] },
  { label: "Teams", icon: <Layers size={20} />, to: "/admin/teams", roles: ["super_admin"] },
  { label: "Coaches", icon: <UserCheck size={20} />, to: "/admin/coaches", roles: ["super_admin"] },
  { label: "Referees", icon: <Shield size={20} />, to: "/admin/referees", roles: ["super_admin"] },
  { label: "Organizations", icon: <Briefcase size={20} />, to: "/admin/organizations", roles: ["super_admin"] },
  { label: "Payments", icon: <CreditCard size={20} />, to: "/admin/payments", roles: ["super_admin"] },
  { label: "Stats", icon: <BarChart size={20} />, to: "/admin/stats", roles: ["super_admin"] },
  { label: "Settings", icon: <Settings size={20} />, to: "/admin/settings", roles: ["super_admin"] },

  // School admin features
  { label: "My School", icon: <School size={20} />, to: "/school-dashboard", roles: ["school_admin"] },
  { label: "Players", icon: <Users size={20} />, to: "/school-dashboard/players", roles: ["school_admin"] },
  { label: "Teams", icon: <Layers size={20} />, to: "/school-dashboard/teams", roles: ["school_admin"] },
  { label: "Tournaments", icon: <Trophy size={20} />, to: "/school-dashboard/tournaments", roles: ["school_admin"] },
  { label: "Payments", icon: <CreditCard size={20} />, to: "/school-dashboard/payments", roles: ["school_admin"] },
  { label: "Settings", icon: <Settings size={20} />, to: "/school-dashboard/settings", roles: ["school_admin"] },
  { label: "Support", icon: <LifeBuoy size={20} />, to: "/school-dashboard/support", roles: ["school_admin"] },

  // Player dashboard (add more as you grow)
  { label: "My Profile", icon: <Users size={20} />, to: "/player-dashboard", roles: ["player"] },
  { label: "My Teams", icon: <Layers size={20} />, to: "/player-dashboard/teams", roles: ["player"] },
  { label: "Tournaments", icon: <Trophy size={20} />, to: "/player-dashboard/tournaments", roles: ["player"] },

  // Add more role sections as needed
];

export default function Sidebar({ user, school, onLogout }) {
  const location = useLocation();

  // Filter links by user role
  const sidebarLinks = SIDEBAR_LINKS.filter((l) =>
    l.roles.includes(user?.role)
  );

  return (
    <aside className="bg-athletiq-blue min-h-screen w-64 flex flex-col items-stretch py-4 shadow-lg">
      {/* Logo */}
      <div className="flex flex-col items-center mb-8">
        <img
          src={require("../assets/logo.png")}
          alt="Athletiq Logo"
          className="h-12 w-auto mb-2"
        />
        <span className="text-2xl font-extrabold text-white tracking-wide">
          ATHLETIQ
        </span>
      </div>

      {/* School info if on school dashboard */}
      {school && (
        <div className="flex flex-col items-center mb-6">
          {school.logo_url && (
            <img
              src={`http://localhost:5000/uploads/${school.logo_url}`}
              alt="School Logo"
              className="h-12 w-12 rounded-full border border-white mb-2 object-cover"
            />
          )}
          <span className="text-lg font-semibold text-white text-center">
            {school.name}
          </span>
        </div>
      )}

      {/* Navigation */}
      <nav className="flex-1 px-2">
        {sidebarLinks.map((link) => (
          <a
            key={link.label}
            href={link.to}
            className={`flex items-center w-full px-4 py-3 mb-2 rounded-lg font-semibold text-base transition
              ${
                location.pathname === link.to
                  ? "bg-athletiq-green text-white"
                  : "text-white hover:bg-athletiq-green/80"
              }
            `}
            style={{ textDecoration: "none" }}
          >
            {link.icon}
            <span className="ml-3">{link.label}</span>
          </a>
        ))}
      </nav>

      {/* User info and logout */}
      <div className="px-4 py-4 border-t border-white/20 flex flex-col gap-2">
        <span className="text-white text-sm font-semibold">
          {user?.full_name || user?.email}
        </span>
        <span className="text-white/70 text-xs capitalize">
          {user?.role?.replace("_", " ")}
        </span>
        <button
          className="mt-2 flex items-center gap-2 text-athletiq-green font-semibold text-base hover:underline"
          onClick={onLogout}
        >
          <LogOut size={18} /> Logout
        </button>
      </div>
    </aside>
  );
}

/**
 * ONBOARDING NOTES:
 * - This sidebar will work for ALL dashboards (super admin, school admin, player, etc.).
 * - Add/remove features by just editing SIDEBAR_LINKS array.
 * - Role-based links; school logo only for school dashboard.
 * - Active state highlighting.
 * - Uses `lucide-react` icons (run: npm i lucide-react)
 * - Place your logo PNG at src/assets/logo.png
 */
