import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

export default function Login() {
  const [role, setRole] = useState('school_admin');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [err, setErr] = useState('');
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    setErr('');
    try {
      let url = '';
      let data = { email, password, role };

      url = `${process.env.REACT_APP_API_URL || 'http://localhost:5000/api/auth/login'}`;

      const res = await axios.post(url, data);
      localStorage.setItem('token', res.data.token);
      localStorage.setItem('role', res.data.role);
      localStorage.setItem('school_id', res.data.school_id || '');
      setTimeout(() => {
        if (res.data.role === 'super_admin') {
          navigate('/admin/dashboard');
        } else {
          navigate('/dashboard');
        }
      }, 500);
    } catch (error) {
      setErr(error?.response?.data?.message || 'Login failed');
    }
  };

  return (
    <div className="flex flex-col items-center justify-center min-h-screen">
      <form className="bg-white p-8 rounded shadow w-full max-w-md" onSubmit={handleLogin}>
        <h2 className="text-xl font-bold mb-4">Login</h2>
        <div className="mb-4">
          <label className="block mb-1">Role:</label>
          <select className="w-full border p-2" value={role} onChange={e => setRole(e.target.value)}>
            <option value="school_admin">School Admin</option>
            <option value="coach">Coach</option>
            <option value="referee">Referee</option>
            <option value="organization">Organization</option>
            <option value="super_admin">Super Admin</option>
            <option value="player">Player</option>
          </select>
        </div>
        <input
          className="w-full border p-2 mb-2"
          placeholder="Email"
          type="email"
          value={email}
          onChange={e => setEmail(e.target.value)}
          required
        />
        <input
          className="w-full border p-2 mb-2"
          placeholder="Password"
          type="password"
          value={password}
          onChange={e => setPassword(e.target.value)}
          required
        />
        {err && <div className="text-red-500 mb-2">{err}</div>}
        <button className="w-full py-2 bg-blue-500 text-white rounded" type="submit">Login</button>
      </form>
    </div>
  );
}
